<?php$options = _WSH()->option();/** * The Template for displaying product archives, including the main shop page which is a post type archive. * * Override this template by copying it to yourtheme/woocommerce/archive-product.php * * @author 		WooThemes * @package 	WooCommerce/Templates * @version     2.0.0 */if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly$meta = _WSH()->get_meta('_sh_layout_settings', get_option( 'woocommerce_shop_page_id' ));$layout = sh_set( $meta, 'layout', 'full' );$layout = sh_set( $_GET, 'layout' ) ? $_GET['layout'] : $layout; if( !$layout || $layout == 'full' || sh_set($_GET, 'layout_style')=='full' ) $sidebar = ''; else$sidebar = sh_set( $meta, 'sidebar') ? $meta['sidebar'] : 'default-sidebar';get_header( 'shop' ); ?><section class="sub-banner">	<div class="overlay">		<div class="container">			<h2><?php woocommerce_page_title(); ?></h2>			<?php echo get_the_breadcrumb(); ?>		</div>	</div></section><!--======= PORTFOLIO =========--><section id="portfolio">	<div class="portfolio portfolio-filter"> 		<!--======= PORTFOLIO ITEMS =========-->		<div class="portfolio-wrapper">			<div class="container"> 		    	<div class="row">		          		          	<div class="col-md-12 col-sm-12 col-xs-12">            						<?php							/**							 * woocommerce_before_main_content hook							 *							 * @hooked woocommerce_output_content_wrapper - 10 (outputs opening divs for the content)							 * @hooked woocommerce_breadcrumb - 20							 */							//do_action( 'woocommerce_before_main_content' );						?>												<div class="widget-title clearfix">							<?php if ( apply_filters( 'woocommerce_show_page_title', true ) ) : ?>						   		<div class="pull-left">			                    	<h3><i class="icon-cart"></i> <?php woocommerce_page_title(); ?></h3>			                	</div>			                <?php endif; ?>			                <?php echo woocommerce_catalog_ordering();?>			                		                </div>		                        		                		                <?php do_action( 'woocommerce_archive_description' ); ?>				        				        <div id="shop-page" class="clearfix">													<?php if ( have_posts() ) : ?>												<?php									/**									 * woocommerce_before_shop_loop hook									 *									 * @hooked woocommerce_result_count - 20									 * @hooked woocommerce_catalog_ordering - 30									 */									//do_action( 'woocommerce_before_shop_loop' );								?>													<?php woocommerce_product_loop_start(); ?>														<?php woocommerce_product_subcategories(); ?>									<ul class="items">									<?php while ( have_posts() ) : the_post(); ?>															<?php wc_get_template_part( 'content', 'product' ); ?>														<?php endwhile; // end of the loop. ?>									</ul>								<?php woocommerce_product_loop_end(); ?>								<div class="clearfix"></div>								<?php									/**									 * woocommerce_after_shop_loop hook									 *									 * @hooked woocommerce_pagination - 10									 */									do_action( 'woocommerce_after_shop_loop' );								?>														<?php elseif ( ! woocommerce_product_subcategories( array( 'before' => woocommerce_product_loop_start( false ), 'after' => woocommerce_product_loop_end( false ) ) ) ) : ?>															<?php wc_get_template( 'loop/no-products-found.php' ); ?>														<?php endif; ?>														<?php									/**									 * woocommerce_after_main_content hook									 *									 * @hooked woocommerce_output_content_wrapper_end - 10 (outputs closing divs for the content)									 */									do_action( 'woocommerce_after_main_content' );								?>														</div>		                </div>									<?php if( $layout == 'right' ): ?>														                <div class="pull-right col-md-4 col-sm-4 col-xs-12">								<div id="sidebar" class="clearfix">									<?php dynamic_sidebar( $sidebar ); ?>								</div>			                </div>										<?php endif; ?>												<?php							/**							 * woocommerce_sidebar hook							 *							 * @hooked woocommerce_get_sidebar - 10							 */							do_action( 'woocommerce_sidebar' );						?>				</div>					</div>		</div>	</div></section><?php wp_enqueue_script('bootstrap-select');?><?php get_footer( 'shop' ); ?>