<?php/** * The template for displaying product content in the single-product.php template * * Override this template by copying it to yourtheme/woocommerce/content-single-product.php * * @author 		WooThemes * @package 	WooCommerce/Templates * @version     1.6.4 */if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly?><?php	/**	 * woocommerce_before_single_product hook	 *	 * @hooked wc_print_notices - 10	 */	 do_action( 'woocommerce_before_single_product' );	 if ( post_password_required() ) {	 	echo get_the_password_form();	 	return;	 }?><div itemscope itemtype="<?php echo woocommerce_get_product_schema(); ?>" id="product-<?php the_ID(); ?>" <?php post_class('clearfix'); ?>>	<div class="col-md-5 col-sm-6">		<div class="item item-h2 cat2 cat3">			<div class="entry">				<?php woocommerce_show_product_images();				//the_post_thumbnail('360x263', array('class'=>'img-responsive' ) ); ?>                                       							</div>		</div><!-- end item -->	</div><!-- end col -->	<div class="col-md-7 col-sm-6">		<?php			/**			 * woocommerce_single_product_summary hook			 *			 * @hooked woocommerce_template_single_title - 5			 * @hooked woocommerce_template_single_rating - 10			 * @hooked woocommerce_template_single_price - 10			 * @hooked woocommerce_template_single_excerpt - 20			 * @hooked woocommerce_template_single_add_to_cart - 30			 * @hooked woocommerce_template_single_meta - 40			 * @hooked woocommerce_template_single_sharing - 50			 */			do_action( 'woocommerce_single_product_summary' );		?>		<div class="shop_desc">			<?php woocommerce_template_single_title(); ?>						<div class="clearfix">				<?php woocommerce_template_single_rating(); ?>			</div>			<div class="bigprice">				<h5><?php woocommerce_template_single_price(); ?></h5>			</div>			<div class="shortdesc">				<?php woocommerce_template_single_excerpt(); ?>				<?php //the_excerpt(); ?>			</div><!-- end desc -->			<br>			<div class="wholesale clearfix paddingmaker">				<?php woocommerce_template_single_add_to_cart(); ?>			</div><!-- wholeasale -->			<br>			<div class="shop_meta">				<?php woocommerce_template_single_meta(); ?>			</div>			<?php woocommerce_template_single_sharing(); ?>		</div>	</div>		<div class="clearfix"></div>    <hr style="width:100%;" />        <div class="col-md-12">		<?php			/**			 * woocommerce_after_single_product_summary hook			 *			 * @hooked woocommerce_output_product_data_tabs - 10			 * @hooked woocommerce_upsell_display - 15			 * @hooked woocommerce_output_related_products - 20			 */			do_action( 'woocommerce_after_single_product_summary' );		?>	</div>		<meta itemprop="url" content="<?php the_permalink(); ?>" /></div><!-- #product-<?php the_ID(); ?> --><?php do_action( 'woocommerce_after_single_product' ); ?>