<?php /** * The Template for displaying product archives, including the main shop page which is a post type archive. * * Override this template by copying it to yourtheme/woocommerce/content-widget-product.php * * @author 		WooThemes * @package 	WooCommerce/Templates * @version     2.5.0 */global $product; ?><li>	<a href="<?php echo esc_url( get_permalink( $product->id ) ); ?>" title="<?php echo esc_attr( $product->get_title() ); ?>">		<?php echo balanceTags($product->get_image()); ?>		<?php echo balanceTags($product->get_title()); ?>	</a>	<?php if ( ! empty( $show_rating ) ) echo balanceTags($product->get_rating_html()); ?>	<?php echo balanceTags($product->get_price_html()); ?></li>